package com.jhm69.money_tracker.ui.income;import androidx.fragment.app.FragmentManager;import androidx.fragment.app.FragmentStatePagerAdapter;import com.jhm69.money_tracker.ui.expenses.ExpensesFragment;import java.util.ArrayList;import java.util.List;public class IncomesViewPagerAdapter extends FragmentStatePagerAdapter {    private final List<IncomeFragment> mFragmentList = new ArrayList<>();    private List<String> pageTitles = new ArrayList<>();    public IncomesViewPagerAdapter(FragmentManager manager) {        super(manager);    }    @Override    public IncomeFragment getItem(int position) {        return mFragmentList.get(position);    }    @Override    public int getCount() {        return mFragmentList.size();    }    public void addFrag(IncomeFragment fragment, String title) {        mFragmentList.add(fragment);        pageTitles.add(title);    }    @Override    public CharSequence getPageTitle(int position) {        return pageTitles.get(position);    }    public List<IncomeFragment> getFragmentList() {        return mFragmentList;    }}